import { StyleSheet, Text, View, Image, useWindowDimensions } from 'react-native';
import React, { useState, useEffect } from 'react';
import Logo from './../../../assets/imgs/Logo.png'
import CustomInput from '../../components/CustomInput';
import CustomButton from '../../components/CustomButton';
import { useNavigation } from '@react-navigation/native';
import { StatusBar } from 'expo-status-bar';
import {
  GoogleSignin,
  GoogleSigninButton,
  statusCodes,
} from '@react-native-community/google-signin';

const SignInScreen = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState({});
  const navigation = useNavigation();

  useEffect(() => {
    GoogleSignin.configure({
      webClientId:
        '599020079103-2qnisn4lq1dh9c1o0aahsc53then4cbk.apps.googleusercontent.com',
      offlineAccess: true,
      forceCodeForRefreshToken: true,
    });
  }, [])

  const onGoogleSignInPressed = async () => {
    try {
        await GoogleSignin.hasPlayServices();
        const userInfo = await GoogleSignin.signIn();
        console.log ( userInfo)
        setUser({ userInfo });
      } catch (error) {
        if (error.code === statusCodes.SIGN_IN_CANCELLED) {
          // user cancelled the login flow
          console.warn('Signin cancelled');
        } else if (error.code === statusCodes.IN_PROGRESS) {
          // operation (e.g. sign in) is in progress already
          console.warn('Signin in progress');
        } else if (error.code === statusCodes.PLAY_SERVICES_NOT_AVAILABLE) {
          // play services not available or outdated
          console.warn('PLAY_SERVICES_NOT_AVAILABLE');
        } else {
          // some other error happened
          navigation.navigate('Home');
        }
      }  
        
    };

  const onSignInPressed = () =>{
    console.warn("SignIn");
    navigation.navigate('Home');
  }
  const onForgotPasswordPressed = () => {
    console.warn('Forgot Password pressed');
  };
  const onSignupPressed = () => {
    console.warn('Sign Up pressed');
    navigation.navigate('SignUp');
  };
  const{height} = useWindowDimensions();
  

  return (
    <View style={styles.root}>
      <StatusBar style="auto" />
      <Text>SignInScreen</Text>
      <Image
        source={Logo}
        style={[styles.logo, {height: height * 0.4}]}
        resizeMode="contain"
      />

      <CustomInput placeholder="Email" value={email} setValue={setEmail} />
      <CustomInput
        placeholder="Password"
        value={password}
        setValue={setPassword}
        secureTextEntry={true}
      />
      <CustomButton text="Sign In" onPress={onSignInPressed} type="SIGNIN" />
      <CustomButton
        text="Forgot Password?"
        onPress={onForgotPasswordPressed}
        type="FORGOTPASSWORD"
      />
      <GoogleSigninButton
        style={{width: 300, height: 55}}
        onPress={onGoogleSignInPressed}
      />
      <CustomButton text="Sign Up " onPress={onSignupPressed} type="SIGNIN" />
    </View>
  );
}

export default SignInScreen

const styles = StyleSheet.create({
  root:{
    backgroundColor: 'white',
    alignItems: 'center',
    pagging: 20,
  },
  logo:{
    width: '70%',
    maxWidth: 300,
    maxHeight:300,
  },
})
